import React, { Component } from 'react'
import { Text, View, Button, ScrollView, Stylesheet } from 'react-native';






class App extends Component {
  state = {
    players: [
      {name: "Player 1" , runner: true},
      {name: "Player 2" ,  runner: false},
      {name: "Player 3" , runner: false},
      {name: "Player 4" , runner: true},
      {name: "Player 5" , runner: false},
      {name: "Player 6" , runner: false},
      {name: "Player 7" , runner: false},
      {name: "Player 8" , runner: false},
      {name: "Player 9" , runner: true},
      {name: "Player 10", runner: false},
      {name: "Player 11", runner: false},
      {name: "Player 12", runner: true},
    ],


    totalRunners: 123,
    totalChasers: 123

  }
  togglePlayer = (index) => {
    let newArray = [...this.state.players];
    if (newArray[index].runner) {
      newArray[index].runner = false

    } else {
      newArray[index].runner = true

    }
    let totalRunners = 0
    let totalChasers = 0
    newArray.forEach (player => {if (player.runner) totalRunners = totalRunners + 1; else totalChasers = totalChasers + 1} )
    this.setState({
      players: newArray,
      totalChasers:totalChasers,
      totalRunners:totalRunners,
    })
  }
  render() {
    const textStyle = {
      color: 'white',
      fontSize: 20,
      fontWeight: 'bold'

    }
    const playerRows = this.state.players.map((player, index) => (
      <View style={{ flexDirection: 'row'}}>
        <Text key={index} style={textStyle}>{player.name}</Text>
        {player.runner &&
          <Button title="Runner" onPress={() => this.togglePlayer(index)}></Button>
        }
        {!player.runner &&
          <Button title="Chaser" onPress={() => this.togglePlayer(index)}> </Button>


        }
      </View>
    ))
    return (
      <View
        style={{
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
          backgroundColor: 'darkblue'
        }}>
        <Text style={textStyle}>Allocate your players</Text>
        <ScrollView style={{
          maxHeight: 200,
          backgroundColor: '#A9A9A9'
        }}>
          {playerRows}
        </ScrollView>
        <Text style={textStyle}>Total runners: {this.state.totalRunners}</Text>
        <Text style={textStyle}>Total chasers: {this.state.totalChasers}</Text>

        <Button title="Start Game"></Button>
        <Button title="Cancel Game"></Button>
      </View>
    )
  }
}
export default App;
